using System.Collections; using System.Collections.Generic; using UnityEngine;  public class EffectKesu : MonoBehaviour {     public float LifTime = 1.0f;      // Use this for initialization  void Start () {         StartCoroutine("WaitAndDestroy");           }       // Update is called once per frame  void Update () {            }      private IEnumerator WaitAndDestroy(){         yield return new WaitForSeconds(LifTime);          Destroy(gameObject);     } } 