using System.Collections; using System.Collections.Generic; using UnityEngine; using UnityEngine.UI;  public class Player1 : MonoBehaviour {     public GameObject getEffect;     public GameObject YugeEffect;     public GameObject P2HP;      public float speed = 100.0F;    // 移動早さ     Animator animator;      /// <summary>     /// 攻撃状態ならtrue     /// </summary>     public bool IsAttacking;      /// <summary>     /// ダメージ状態ならtrue     /// </summary>     public bool IsDamaged;      /// <summary>     /// 死んだらtrue     /// </summary>     public bool IsDead;      [SerializeField]     Player2 Enemy;      //自分のHPバー     public GameObject hpbar;     int hp;      // Use this for initialization     void Start()     {         animator = GetComponent<Animator>();     }      // Update is called once per frame     void Update()     {         // animator.SetBool("damage", isDamaged);          //ダーメージを受けていたら何もしない         //if (isDamaged)         //    return;         //死んでたら何もしない         if (IsDead)             return;          //        Debug.Log("Inputed");         // 矢印キーの入力情報を取得         var h = Input.GetAxis("Horizontal");         var v = Input.GetAxis("Vertical");          // 移動する向きを作成する         Vector2 direction = new Vector2(h, v).normalized;          // 移動する向きとスピードを代入          GetComponent<Rigidbody2D>().velocity = direction * speed;          if (Input.GetButtonDown("Attack"))         {             Debug.Log("Attack");             animator.SetTrigger("Attack");         }           if (Input.GetKeyDown(KeyCode.Z))         {
            //animator.SetBool("attack", true);
            animator.SetTrigger("Attack");         }         //else         //{         //    animator.SetBool("attack", false);         //}            if (Input.GetButtonDown("Tornade"))         {             Debug.Log("Tornade");             animator.SetTrigger("Tornade");         }           if (Input.GetKeyDown(KeyCode.X))         {             // animator.SetBool("tornado", true);             animator.SetTrigger("Tornade");         }         //else         //{         //    animator.SetBool("tornado", false);         //}          if (Input.GetButtonDown("Combo"))         {             Debug.Log("Combo");             animator.SetTrigger("Combo");         }          if (Input.GetKeyDown(KeyCode.C))         {             // animator.SetBool("combo", true);             animator.SetTrigger("Combo");         }         //else         //{         //    animator.SetBool("combo", false);         //}       }      //ダメージを受ける     public void Damage()     {         //  既にダメージを受けていたら何もしない         if (IsDamaged)             return;          //死んでたら何もしない         if (IsDead)             return;                  //Debug.Log("Player 1 Damaged!");         IsDamaged = true;         // animator.SetBool("damage", isDamaged);         animator.SetTrigger("Damage");         StartCoroutine(damaged());        }      // ダメージを受けたフラグを管理するコルーチン       private IEnumerator damaged()     {         // n秒待機してから、ダメージ状態を解除する         yield return new WaitForSeconds(1.0f);         IsDamaged = false;     }      //ダメージを受けました     void OnCollisionStay2D(Collision2D col)     {         if (col.gameObject != Enemy.gameObject)             return;          // Debug.Log("Hit");          //ダメージ中は追加ダメージを受けない         if (IsDamaged)             return;          //死んでいたらダメージはない         if (IsDead)             return;          //敵が攻撃中ではないなら、ダメージにはならない         if (!Enemy.IsAttacking)             return;          damaged(3);          if( hp > 0 )         {             Damage();         }         else         {             //HPが０以下なので、「そこまで！」             GameManager.Instance.GameSet(2);             animator.SetBool("die", true);         }     }      public void damaged(int v){         var slider = hpbar.GetComponent<Slider>();         hp = (int)slider.value;         hp -= v;         slider.value = hp;         P2HP.GetComponent<SetSpriteToHP>().addHP();     }      private void OnCollisionEnter2D(Collision2D coll)     {          if (coll.gameObject.tag == "Star")         {             Instantiate(getEffect, coll.gameObject.transform.position, Quaternion.identity);              Destroy(coll.gameObject);         }     }  } 